const useStyles = makeStyles(() => ({
    canvas_container: {
      backgroundColor: 'lightgray',
    }
}));

export default function WaveSvg(props) {
    const classes = useStyles();
    const [state, setState] = useState(initialState);
    const {dragging, waveData, initialDragX} = state;

    const startedDragging = (e) => {
        if(dragging) {

        } else {
            setState({...state, dragging: true, initialDragX: e.clientX});
        }
    };

    const dragWave = (e) => {
        if(dragging) {
            const x = e.clientX;
            const translatedWave = waveData.slice(0);
            translatedWave.rotate(initialDragX-x);
            setState({...state, waveData: translatedWave});
        } else {
            //console.log('not dragging');
        }
    }

    const stoppedDragging = (e) => {
        if(dragging) {
            setState({...state, dragging: false, initialDragX : null}); 
        }
    };

    const getPath=  () => {
        let path = 'M50,60';
        for(let i =0;i<1000;i++) {
            path = `${path} L${50+i},${10+waveData[i]}`
        }
        return (<path d={path} />);
    };

    return (        
    <div container="true">
        <svg
            style= {{
                fill:'none',
                stroke: 'black',
                strokeWidth: '2'
            }}
            className={classes.canvas_container} width="1100" height="120" 
            onMouseDown={startedDragging} 
            onMouseUp={stoppedDragging} 
            onMouseOut={stoppedDragging}
            onMouseMove={dragWave} >
                {getPath()}
        </svg> 
    </div>);
}

Array.prototype.rotate = (function() {
    var unshift = Array.prototype.unshift,
        splice = Array.prototype.splice;

    return function(count) {
        var len = this.length >>> 0,
            count = count >> 0;

        unshift.apply(this, splice.call(this, count % len, len));
        return this;
    };
})();